'use strict';

exports.name = 'Event';

exports.setUp = (app, mongoose) =>
{
  const schema = new mongoose.Schema({
    type: {
      type: String,
      trim: true,
      required: true
    },
    severity: {
      type: String,
      enum: ['debug', 'info', 'success', 'warning', 'error'],
      default: 'info'
    },
    time: {
      type: Number,
      default: Date.now
    },
    user: {
      type: Object
    },
    data: {
      type: Object
    }
  }, {
    id: false
  });

  schema.statics.TOPIC_PREFIX = 'events';

  schema.index({time: -1});
  schema.index({severity: 1, time: -1});
  schema.index({type: 1, time: -1});
  schema.index({'user._id': 1, time: -1});

  return schema;
};
