'use strict';

exports.name = 'Symbol';

exports.setUp = (app, mongoose) =>
{
  const symbolSchema = mongoose.Schema({
    _id: {
      type: String,
      required: true
    },
    group: {
      type: String,
      trim: true
    },
    name: {
      type: String,
      trim: true
    }
  }, {
    id: false
  });

  symbolSchema.statics.TOPIC_PREFIX = 'symbols';
  symbolSchema.statics.BROWSE_LIMIT = 1000;

  mongoose.model(exports.name, symbolSchema);
};
