'use strict';

module.exports = (app, module, userSchema) =>
{
  userSchema.path('symbol', {
    type: String,
    default: null,
    trim: true
  });

  userSchema.index({symbol: 1});

  userSchema.statics.dataAnonymizers.push(user =>
  {
    user.symbol = null;
  });

  userSchema.statics.EDITABLE_PROPERTIES.symbol = true;
};
