/* eslint-disable no-var */

'use strict';

let collator = {
  compare(a, b) { return a.localeCompare(b); },
  resolvedOptions() { return {}; }
};

if (global.Intl && Intl.Collator)
{
  collator = new Intl.Collator('pl', {
    usage: 'sort',
    sensitivity: 'base',
    numeric: true,
    ignorePunctuation: true
  });
}

module.exports = function localeCompare(a, b)
{
  return collator.compare(a, b);
};
