'use strict';

module.exports = err =>
{
  if (err.isAxiosError)
  {
    err.request = {
      method: err.config.method,
      url: err.config.url,
      data: err.config.data
    };

    delete err.config;

    if (err.response)
    {
      delete err.response.request;
      delete err.response.config;
    }
  }

  return err;
};
