'use strict';

const jsonDeepEqual = require('./jsonDeepEqual');

module.exports = function objectDiffLeft(newObj, oldObj)
{
  const difference = {};
  let different = false;

  for (const key of Object.keys(newObj))
  {
    if (!jsonDeepEqual(newObj[key], oldObj[key]))
    {
      different = true;
      difference[key] = [newObj[key], oldObj[key]];
    }
  }

  return different ? difference : null;
};
